if(navigator.userAgent.match(/Alipay/i)){window.location.href=setting.aliUrl;}else if(navigator.userAgent.match(/MicroMessenger\//i)){document.getElementById("wechat-url").src=setting.qrcodeApi+urlEncode(setting.wechatUrl);document.getElementById("code-wechat").style.display="block";}else if(navigator.userAgent.match(/QQ\//i)){document.getElementById("qq-url").src=setting.qrcodeApi+urlEncode(setting.qqUrl);document.getElementById("code-qq").style.display="block";}else{document.getElementById("page-url").src=setting.qrcodeApi+urlEncode(window.location.href);document.getElementById("code-all").style.display="block";}
function urlEncode(String){return encodeURIComponent(String).replace(/'/g,"%27").replace(/"/g,"%22");}